export function fetchData(category) {
    switch (category.replace(/\s/g, "_")) {
        case "Punto_cadena":
            return Punto_cadena;
        case "Punto_bajo":
            return Punto_bajo;
        case "Punto_enano":
            return Punto_enano;
        case "Anillo_base":
            return Anillo_base;
        case "Punto_alto":
            return Punto_alto;
        case "Punto_medio_alto":
            return Punto_medio_alto;
        case "Punto_alto_doble":
            return Punto_alto_doble;
        case "Manta_oveja_bebe":
            return Manta_oveja_bebe;
        default:
            return Punto_cadena;
    }
}

export async function fetchImages(category) {
    return queryImg().then((data) => {
        const items = data.mediaItems.edges;
        const result = [];

        items.forEach((item) => {
            const mediaName = handleMediaNames(item.node.title);
            if (mediaName.trim() == category.trim()) {
                result.push(item.node.sourceUrl);
            }
        })

        return result;
    })
}

function handleMediaNames(title) {
    const words = title.split("-");
    words.shift();
    const join = words.join(' ');
    const capitalize = join.charAt(0).toUpperCase() + join.slice(1);
    const result = capitalize.replace(/\d+/g, '');

    return result;
}

async function queryImg() {
    const query =
        `
        {
            mediaItems {
                edges {
                cursor
                node {
                    id
                    title
                    altText
                    sourceUrl
                }
                }
            }
        }
    `
    const res = await fetch("https://mollydigital.manu-scholz.com/graphql", {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
            query,
        }),
    })
    const json = await res.json();
    return json.data;
}